sys_script.script:
//Function to Modify the Saving Fund when create the Siniestro
//Call the function

//Create The function
(function executeRule(current, previous /*null when async*/)  {
	//Call siniestros
	var fondocall = new GlideRecord('x_16940_siniestros_fondo_de_reserva');
	fondocall.addQuery('sys_id', current.u_numero_fondo_de_ahorro.sys_id);
	fondocall.query();
	if(fondocall.next())
		{
    //var fondocall = current.u_numero_fondo_de_ahorro.getRefRecord();
	var currency = 0;
	var asegurado = 0;
	// gs.addInfoMessage("obteniendo la referencia"+current.u_numero_fondo_de_ahorro.getDisplayValue());

    //fondocall.monto_acumulado +=parseFloat(current.u_monto_asegurado);
	gs.addInfoMessage("obteniendo la referencia"+current.u_monto_asegurado);
	gs.addInfoMessage('Fondo Total: ' + fondocall.fondo_de_reserva);
	currency = parseFloat(fondocall.fondo_de_reserva);
	//currency = fondocall.fondo_de_reserva;
	gs.addInfoMessage('Currency Before: '+ currency);
	asegurado = parseFloat(current.u_monto_asegurado);
	//asegurado = current.u_monto_asegurado;
	gs.addInfoMessage('Monto Asegurado: '+ asegurado);
	currency = currency - asegurado;
	gs.addInfoMessage('Currency After: '+ currency);
	fondocall.fondo_de_reserva = 'MXN;' + currency;

    fondocall.update();
	var calculada = parseFloat(fondocall.fondo_de_reserva);
	current.auxiliar = calculada;
	//Message to know if the update is good!
    gs.addInfoMessage("Updating Saving Fund");
		}
})(current, previous);
