sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here	
	var gr = new GlideRecord('x_16940_siniestros_resumen_mensual');
	var cur = parseFloat(current.u_monto_asegurado);
	var fechaMonth = new GetMonthResumen();
	var requestDate = new GlideDate();	
	var year = '';
	var month = 0;
	var checkMonth = '';
	var currency;
	var pending;
	var closed;


	requestDate = current.getValue('fecha_del_siniestro');
	checkMonth = requestDate.slice(5,7);
	month = fechaMonth.getMonthResumen(checkMonth);
	year = requestDate.slice(0,4);
	gr.addQuery('mes', month);
	gr.addQuery('ano', year);
	gr.query();
	if(gr.next())
		{
			if(current.tracking == '9')
			{
				closed = parseFloat(gr.monto_pagado_incluye_gastos);
				closed += cur;
				gr.monto_pagado_incluye_gastos = "USD;" + closed;
				gr.no_casos_pagados += 1;
			}

			else
			{
				pending = parseFloat(gr.monto_pendiente);
				pending += cur;
				gr.monto_pendiente = "USD;" + pending;
				gr.no_casos_pendientes += 1;
			}
			currency = parseFloat(gr.monto_presentado_por_cliente);			
			currency += cur;			
			gr.monto_presentado_por_cliente = "USD;" + currency;
			gr.no_casos_reportados += 1;
			gr.update();
		}

	else
		{
 			gr.initialize();

 			if(current.tracking == '9')
 			{
 				gr.no_casos_pagados = 1;
				gr.no_casos_pendientes = 0;
				gr.monto_pagado_incluye_gastos = current.u_monto_asegurado;
			}

			else
			{
				gr.no_casos_pagados = 0;
				gr.no_casos_pendientes = 1;
				gr.monto_pendiente = current.u_monto_asegurado;
			}
			gr.no_casos_reportados = 1;
			gr.monto_presentado_por_cliente = current.u_monto_asegurado;
			gr.mes = month;
			gr.ano = year;
			gr.insert();
		}		
})(current, previous);
