sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var gr = new GlideRecord('x_16940_siniestros_resumen_mensual');
	var prevGr = new GlideRecord('x_16940_siniestros_resumen_mensual');
	var requestDate = new GlideDate();
	var pastDate = new GlideDate();
	var year = '';
	var prevYear = '';
	var month = 0;
	var prevMonth = 0;
	var checkMonth = '';
	var pastMonth = '';
	var cur = parseFloat(current.u_monto_asegurado);
	var prev = parseFloat(previous.u_monto_asegurado);
	var closed_currency;
	var pending_currency;
	var currency;
	var fechaMonth = new GetMonthResumen();
	//var previousResumen = new UpdatePreviousResumen();


	requestDate = current.getValue('fecha_del_siniestro');
	pastDate = previous.getValue('fecha_del_siniestro');
	pastMonth = pastDate.slice(5,7);
	checkMonth = requestDate.slice(5,7);

	month = fechaMonth.getMonthResumen(checkMonth);
	if(pastMonth != checkMonth)
		{
			prevYear = pastDate.slice(0,4);
			prevGr.addQuery('ano', prevYear);
			prevMonth = fechaMonth.getMonthResumen(pastMonth);

			prevGr.addQuery('mes', prevMonth);
			prevGr.query();
			if(prevGr.next())
				{	

					var oldCurrency = parseFloat(previous.u_monto_asegurado);
					var total = parseFloat(prevGr.monto_presentado_por_cliente);
					var closed = parseFloat(prevGr.monto_pagado_incluye_gastos);
					var pending = parseFloat(prevGr.monto_pendiente);
					prevGr.no_casos_reportados -= 1;
					total -= oldCurrency;

					if(previous.tracking == '9')
						{
							prevGr.no_casos_pagados -= 1;
							closed -= oldCurrency;
						}

					else
						{
							prevGr.no_casos_pendientes -= 1;
							pending -= oldCurrency;
						}
					prevGr.monto_presentado_por_cliente = "USD;" + total;
					prevGr.monto_pagado_incluye_gastos = "USD;" + closed;
					prevGr.monto_pendiente = "USD;" + pending;
					prevGr.update();
				}
			gr.addQuery('mes', month);
			year = requestDate.slice(0,4);
			gr.addQuery('ano',year);
			gr.query();
			if(gr.next())
				{
					if(current.tracking == '9')
						{
							gr.no_casos_pagados += 1;
							//gr.no_casos_pendientes -= 1;
							closed_currency = parseFloat(gr.monto_pagado_incluye_gastos);
							closed_currency += cur;
							//pending_currency -= cur;
							gr.monto_pagado_incluye_gastos = "USD;" + closed_currency;
							//gr.monto_pendiente = "USD;" + pending_currency;
						}

					else
						{
							pending_currency = parseFloat(gr.monto_pendiente);
							pending_currency += cur;
							gr.monto_pendiente = "USD;" + pending_currency;
							gr.no_casos_pendientes += 1;
						}
					currency = parseFloat(gr.monto_presentado_por_cliente);
					currency += cur;
					gr.monto_presentado_por_cliente = "USD;" + currency;
					gr.no_casos_reportados += 1;
					gr.update();
				}
			else
				{
						gr.initialize();

						if(current.tracking == '9')
							{
								gr.no_casos_pagados = 1;
								gr.no_casos_pendientes = 0;
								gr.monto_pagado_incluye_gastos = current.u_monto_asegurado;
							}

						else
							{
								gr.no_casos_pagados = 0;
								gr.no_casos_pendientes = 1;
								gr.monto_pendiente = current.u_monto_asegurado;
							}
						gr.no_casos_reportados = 1;
						gr.monto_presentado_por_cliente = current.u_monto_asegurado;
						gr.mes = month;
						gr.ano = year;
						gr.insert();
				}
		}

	else
		{
			gr.addQuery('mes', month);
			year = requestDate.slice(0,4);
			gr.addQuery('ano',year);
			gr.query();
			if(gr.next())
				{
					pending_currency = parseFloat(gr.monto_pendiente);

					if(current.tracking == '9' && previous.tracking != '9')
						{
							gr.no_casos_pagados += 1;
							gr.no_casos_pendientes -= 1;
							closed_currency = parseFloat(gr.monto_pagado_incluye_gastos);
							closed_currency += cur;
							pending_currency -= cur;
							gr.monto_pagado_incluye_gastos = "USD;" + closed_currency;
							gr.monto_pendiente = "USD;" + pending_currency;
						}

					else
						{
							pending_currency += cur - prev;
							gr.monto_pendiente = "USD;" + pending_currency;
						}
					currency = parseFloat(gr.monto_presentado_por_cliente);
					currency += cur - prev;
					gr.monto_presentado_por_cliente = "USD;" + currency;
					gr.update();
				}
		}
})(current, previous);
