sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var gr = new GlideRecord('x_16940_siniestros_resumen_mensual');
	var requestDate = new GlideDate();
	var year = '';
	var requestMonth = '';
	var month = 0;
	var fechaMonth = new GetMonthResumen();
	var oldCurrency = 0;
	var total = 0;
	var closed = 0;
	var pending = 0;

	requestDate = current.getValue('fecha_del_siniestro');
	year = requestDate.slice(0,4);
	requestMonth = requestDate.slice(5,7);
	month = fechaMonth.getMonthResumen(requestMonth);
	gr.addQuery('ano', year);
	gr.addQuery('mes', month);
	gr.query();
	if(gr.next())
		{
			oldCurrency = parseFloat(current.u_monto_asegurado);
			total = parseFloat(gr.monto_presentado_por_cliente);
			closed = parseFloat(gr.monto_pagado_incluye_gastos);
			pending = parseFloat(gr.monto_pendiente);
			gr.no_casos_reportados -= 1;
			total -= oldCurrency;

			if(current.tracking == '9')
				{
					gr.no_casos_pagados -= 1;
					closed -= oldCurrency;
				}

			else
				{
					gr.no_casos_pendientes -= 1;
					pending -= oldCurrency;
				}
			gr.monto_presentado_por_cliente = "USD;" + total;
			gr.monto_pagado_incluye_gastos = "USD;" + closed;
			gr.monto_pendiente = "USD;" + pending;
			gr.update();
		}

})(current, previous);
