sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }	
	var siniestroDate = g_form.getValue('fecha_del_siniestro');
    var siniestro_year = siniestroDate.slice(0,4);
    var siniestro_month = siniestroDate.slice(5,7);
	var siniestro_day = siniestroDate.slice(8,10);

	var requestDate = g_form.getValue('u_fecha_de_informe_preliminar');
    var request_year = requestDate.slice(0,4);
    var request_month = requestDate.slice(5,7);
	var request_day = requestDate.slice(8,10);

	if((siniestro_year > request_year) || (siniestro_year == request_year && siniestro_month > request_month) || (siniestro_year == request_year && siniestro_month == request_month && siniestro_day > request_day))
		{
			g_form.setValue('u_fecha_de_informe_preliminar', '');
			g_form.showFieldMsg('u_fecha_de_informe_preliminar', 'Date must not be in the past', 'error', false);
		}	
}
