sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var ektValue = '';
	var ga = new GlideAjax('CheckReferenciaEKT');
	ga.addParam('sysparm_name','checkReferencia');
	ga.addParam('sysparm_ekt',g_form.getValue('u_referencia_ekt'));
	ga.getXML(selectLan);

	function selectLan(response) 
	{
		var answer = response.responseXML.documentElement.getAttribute('answer');
		if(answer == 'true')
			{
				ektValue = g_form.getValue('u_referencia_ekt');
				g_form.clearValue('u_referencia_ekt');
				g_form.showErrorBox('u_referencia_ekt', '\''+ektValue + '\' Ya Existe');
			}
	}


// 	}
   //Type appropriate comment here, and begin script below

}
