 var monto_ajustado = g_form.getDecimalValue('u_monto_ajustado');
	var deductible = g_form.getDecimalValue('u_deducible');
	var coaseguro = g_form.getDecimalValue('u_coaseguro');
	var depreciacion = g_form.getDecimalValue('u_depreciacion');
	var monto_d_ajuste = g_form.getDecimalValue('u_monto_despues_ajuste');

	//var total = sub - deductible - coaseguro - depreciacion;
	var total = monto_ajustado - deductible - coaseguro - depreciacion;


	g_form.setValue('u_monto_despues_ajuste', 'MXN;' + total);
}
