sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var subtotal = g_form.getDecimalValue('u_subtotal');
	var ajustesUp = g_form.getDecimalValue('u_ajuste_gasto');
	var ajustesDown = g_form.getDecimalValue('u_ajuste_gasto_negativo');
	var iva = subtotal * 0.16 ;
	var iva_up = ajustesUp * 0.16;
	var iva_down = ajustesDown * 0.16;
	//Summary of IVA
	var sum_iva = iva + iva_up - iva_down;
	var total = subtotal + sum_iva;

	g_form.setValue('u_iva', 'MXN;' + sum_iva);
	g_form.setValue('u_honorarios_iva', 'MXN;' + total);
	g_form.setValue('u_total', 'MXN;' + total); 
}
