function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//2018-08-21
	var date = g_form.getValue('fecha_del_siniestro');
	var year = date.slice(0,4);
	var month = date.slice(5,7);
	var day = date.slice(8,10);
	var yearInt = parseInt(year);
	var monthInt = parseInt(month);
	var dayInt = parseInt(day);

	var time = g_form.getValue('u_time_siniestro');
	var hour = time.slice(0,2);
	var minute = time.slice(3,4);
	var second = time.slice(5,16);
	var hourInt = parseInt(time);
	var minuteInt = parseInt(minute);
	var secondInt = parseInt(second);
	//out of years
	if(yearInt < 2018 || yearInt > 2019)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}
	//Out of year 2018 and month
	else if(yearInt == 2018 && monthInt < 3)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}

	else if(yearInt == 2018 && monthInt == 3 && dayInt < 2)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}

	else if(yearInt == 2018 && monthInt == 3 && dayInt == 2 && hourInt < 12)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}

	else if(yearInt == 2019 && monthInt > 3)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}

	else if(yearInt == 2019 && monthInt == 3 && dayInt > 2)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}
	else if(yearInt == 2019 && monthInt == 3 && dayInt == 2 && hourInt > 12)
		{
			alert('No se puede generar un siniestro fuera periodo del fondo se reserva');
		    g_form.setValue('fecha_del_siniestro','');
			 g_form.setValue('u_time_siniestro','');
		}



}
