	 while (gr.next()){

		total = parseFloat(total) + parseFloat(gr.u_monto_asegurado.getCurrencyValue());//Push the values inside the array separated
		 //total.setValue('USD;'+total);

	  }	

gs.addInfoMessage("Fondo de Reserva Consumido por Siniestros Actual:" +total);

		return total;
		//total
	},

		getSavingFund: function(){
