sys_script_include.script:
var getActualDate = Class.create();
getActualDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
		actualdate: function () 
		{ 
			//var fecha = 'hola';
			var date = new Date();
			var day= date.getDate();
			var month= date.getMonth()+1;
			var year= date.getFullYear();
			var hours = date.getHours()+2;
			var minutes = date.getMinutes();
			var seconds = date.getSeconds();
			//Validaciones
			if(day<10){
				day='0'+day;
			}
			if(month<10){
				month='0'+month;
			}
			if(hours<10){
				hours='0'+hours;
			}if(minutes<10){
				minutes='0'+minutes;
			}if(seconds<10){
				seconds='0'+seconds;

			}

			var actual = year+'-'+month+'-'+day;
			//gs.addInfoMessage("Iniciando Metodo de Fecha"+hours+':'+minutes+':'+seconds);
		return actual; 
		},
    type: 'getActualDate'
});
