sys_script_include.script:
var GetMonthResumen = Class.create();
GetMonthResumen.prototype = {
    initialize: function() {},

	getMonthResumen: function(checkMonth)
	{
		var month = 0;
		switch(checkMonth)
		{
			case '01':
				month = 1;
				break;
			case '02':
				month = 2;
				break;
			case '03':
				month = 3;
				break;
			case '04':
				month = 4;
				break;
			case '05':
				month = 5;
				break;
			case '06':
				month = 6;
				break;
			case '07':
				month = 7;
				break;
			case '08':
				month = 8;
				break;
			case '09':
				month = 9;
				break;
			case '10':
				month = 10;
				break;
			case '11':
				month = 11;
				break;
			case '12':
				month = 12;
				break;
			default:
				month = 0;
				break;
		}
		return month;
	},

    type: 'GetMonthResumen'
};
