sys_ui_action.script:
//Script to get the sys_id of the form an set in the current 
createAssign();

function createAssign() {
//Create the GlideRecord to call the table
	var estado = new GlideRecord('x_16940_siniestros_estado');
   var asg = new GlideRecord("x_16940_siniestros_apertura_reservas");//Apertura Reservas
	estado.addQuery('nombre',current.numero_sucursal.estado);

 	estado.query();
 	if(estado.next())
 		{
 			asg.u_estado = estado.getUniqueValue();
 		}
	var municipio = new GlideRecord('x_16940_siniestros_municipio');
	municipio.addQuery('nombre',current.numero_sucursal.municipio);
	municipio.query();
	if(municipio.next())
		{
			asg.u_municipio = municipio.getUniqueValue();
		}

	//asg.u_lugar_ocurrencia=current.u_nombre_sucursal;
	if(current.numero_sucursal.u_tienda==""){
		asg.u_lugar_ocurrencia=current.u_nombre_sucursal;
	}else if(current.u_nombre_sucursal==""){
		asg.u_lugar_ocurrencia=current.numero_sucursal.u_tienda;
	}

	//asg.u_lugar_ocurrencia=current.numero_sucursal.u_tienda;
	//asg.u_estado=current.numero_sucursal.estado;
	//gs.addInfoMessage("Estado " + asg.u_estado + " Asignado.");
	//asg.u_municipio=current.numero_sucursal.municipio;
	//gs.addInfoMessage("Muncipio " + asg.u_municipio + " Asignado.");
	asg.u_codigo_postal=current.numero_sucursal.cp;
	asg.numero_de_sucursal=current.numero_sucursal;
	asg.u_valor_ubicacion=current.numero_sucursal.valor_ubicacion;
	asg.u_direccion=current.numero_sucursal.direccion;
	//Asignar en el Actual Siniestro
	var sysID = asg.insert();
	current.numero_de_reserva = sysID;

	var mySysID = current.update();

gs.addInfoMessage("Reserva " + asg.number + " creada.");
action.setRedirectURL(asg);
action.setReturnURL(current);



	current.u_estado=3;
	//aper.update();
	current.update();

}
